import React, { Component } from "react";
import { Table, Button, Spinner, Badge } from "react-bootstrap";
import { Link } from "react-router-dom";
import { departmentDetails } from "../services/Departments";
import withRouter from '../services/withRouter';
import { deleteStudent } from "../services/Student";

class ViewDepartment extends Component {
  state = {
    isLoading: false,
    department: "",
    students: [],
    deleting: false,
    resStatus: false
  };

  componentDidMount() {
    this.getDepartmentDetails();
  }

  getDepartmentDetails = async () => {
    this.setState({ isLoading: true });
    const id = this.props.params.id;
    const res = await departmentDetails(id);
    
    if (res.success) {
      this.setState({
        department: res.data,
        students: res.data.students,
        isLoading: false,
      });
    } else {
      this.setState({
        isLoading: false,
      });
      alert("something went wrong");
    }
  };

  deleteStudent = async (id) => {

    if(confirm("Are you sure?")){
      this.setState({
        deleting: true,
      });
      const del = await deleteStudent(id);

      if(del.success){
        this.getDepartmentDetails();
        this.setState({
          resStatus: true
        });
      }
      this.setState({
        deleting: false,
      });
    }
    
  }

  render() {
    
    return (
      <>
        <h1>
          View Department
          <Link to="/departments">
            <Button variant="primary" className="btn-sm">
              Back
            </Button>
          </Link>
        </h1>

        {this.state.isLoading ? (
          <div className="text-center">
            <Spinner animation="border" role="status">
              <span className="visually-hidden">Loading...</span>
            </Spinner>
          </div>
        ) : (
          <div>
            {this.state.resStatus &&
                <p className="alert alert-success">Your student has been deleted successfully</p>
            }
            
            <Table striped bordered hover variant="dark">
              <tbody>
                <tr>
                  <th>Name</th>
                  <td>{this.state.department.name}</td>
                </tr>
                <tr>
                  <th>Address</th>
                  <td>{this.state.department.address}</td>
                </tr>
                <tr>
                  <th>Status</th>
                  <td> 
                    {this.state.department.status ==1 ?
                        (
                          <Badge bg="success">Active</Badge>
                        ):(
                          <Badge bg="danger">Inactive</Badge>
                        )
                    }
                  </td>
                </tr>
              </tbody>
            </Table>

            <h1>Students

              <Link to={`/create-student/${this.state.department.id}`}><Button variant="primary">Create Student</Button></Link>
            </h1>
            {this.state.deleting &&
                <Spinner animation="border" role="status">
                  <span className="visually-hidden">Loading...</span>
                </Spinner>
            }
            
            { this.state.students.length > 0 &&
              <Table striped bordered hover variant="dark">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Roll</th>
                    <th>Phone</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.students.map((student, index) => (
                    <tr key={index}>
                      <td>{student.name}</td>
                      <td>{student.roll}</td>
                      <td>{student.phone}</td>
                      <td>
                        <Link to={`/edit-student/${this.state.department.id}/${student.id}`}><Button variant="warning" className="btn-sm mr-2">Edit</Button></Link>
                        <Button variant="danger" className="btn-sm" onClick={ (e) => this.deleteStudent(student.id)}>
                          Delete
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            }
          </div>
        )}
      </>
    );
  }
}

export default withRouter(ViewDepartment);
