import React, { Component } from 'react';
import { Form, FloatingLabel, Button } from 'react-bootstrap';
import { storeDepartment } from '../services/Departments';

class CreateDepartment extends Component {
    state = {
        name: '',
        address: '',
        status: 0,
        isLoading: false,
        errors: [],
        submitted: false
    }

    onChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        });
    }

    submitForm = async (e) => {
        e.preventDefault();
        const { history } = this.props;

        this.setState({isLoading: true});
        const postbody = {
            name: this.state.name,
            address: this.state.address,
            status: this.state.status,
            user_id: 1,
        }

        const res = await storeDepartment(postbody);

        if(res.success){
            this.setState({
                name: '',
                address: '',
                status: '',
                isLoading: false,
                submitted: true
            });
            history.push("/project-list");
        }else{
            this.setState({
                isLoading: false,
                errors: res.errors
            });
        }
    }

    render(){
        return(
            <>
                <h1>Create Department</h1>
                <Form onSubmit={this.submitForm}>
                    {this.state.submitted &&
                        <p className="alert alert-success">Your department has been created successfully</p>
                    }
                    <FloatingLabel controlId="floatingInput" label="Department Name" className="mb-3">
                        <Form.Control type="text" placeholder="Enter Department Name" name="name" value={this.state.name} onChange={(e) => this.onChange(e)} />
                    </FloatingLabel>
                    {this.state.errors && this.state.errors.name && 
                        <p className="alert alert-danger">{this.state.errors.name[0]}</p>
                    }
                    <FloatingLabel controlId="floatingInput" label="Address" className="mb-3">
                        <Form.Control type="text" placeholder="Enter Address" name="address" value={this.state.address} onChange={(e) => this.onChange(e)}/>
                    </FloatingLabel>
                    {this.state.errors && this.state.errors.address && 
                        <p className="alert alert-danger">{this.state.errors.address[0]}</p>
                    }
                    <Form.Check type="checkbox" id="status" label="status" name="status" value="1" onChange={(e) => this.onChange(e)}/>
                    
                    {this.state.isLoading && 
                        <Button variant="primary" type="button" className="mt-2" disabled>Saving...</Button>
                    }
                    
                    {!this.state.isLoading && 
                        <Button variant="primary" type="submit" className="mt-2">Save</Button>
                    }
                </Form>
            </>
        )
    }
}

export default CreateDepartment;
