import React, { Component } from "react";
import { Table, Button, Spinner, Badge } from "react-bootstrap";
import { Link } from "react-router-dom";
import { departmentDetails } from "../services/Departments";
import withRouter from '../services/withRouter';

class ViewDepartment extends Component {
  state = {
    isLoading: false,
    department: "",
    students: [],
  };

  componentDidMount() {
    this.getDepartmentDetails();
  }

  getDepartmentDetails = async () => {
    this.setState({ isLoading: true });
    const id = this.props.params.id;
    const res = await departmentDetails(id);
    
    if (res.success) {
      this.setState({
        department: res.data,
        students: res.data.students,
        isLoading: false,
      });
    } else {
      this.setState({
        isLoading: false,
      });
      alert("something went wrong");
    }
  };

  render() {
    
    return (
      <>
        <h1>
          View Department
          <Link to="/departments">
            <Button variant="primary" className="btn-sm">
              Back
            </Button>
          </Link>
        </h1>

        {this.state.isLoading ? (
          <div className="text-center">
            <Spinner animation="border" role="status">
              <span className="visually-hidden">Loading...</span>
            </Spinner>
          </div>
        ) : (
          <div>
            <Table striped bordered hover variant="dark">
              <tbody>
                <tr>
                  <th>Name</th>
                  <td>{this.state.department.name}</td>
                </tr>
                <tr>
                  <th>Address</th>
                  <td>{this.state.department.address}</td>
                </tr>
                <tr>
                  <th>Status</th>
                  <td> 
                    {this.state.department.status ==1 ?
                        (
                          <Badge bg="success">Active</Badge>
                        ):(
                          <Badge bg="danger">Inactive</Badge>
                        )
                    }
                  </td>
                </tr>
              </tbody>
            </Table>

            <h1>Students

              <Link to={`/create-student/${this.state.department.id}`}><Button variant="primary">Create Student</Button></Link>
            </h1>
            { this.state.students.length > 0 &&
              <Table striped bordered hover variant="dark">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Roll</th>
                    <th>Phone</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.students.map((student, index) => (
                    <tr key={index}>
                      <td>{student.name}</td>
                      <td>{student.roll}</td>
                      <td>{student.phone}</td>
                      <td>
                        <Button variant="warning">Edit</Button>
                        <Button variant="danger">Delete</Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            }
          </div>
        )}
      </>
    );
  }
}

export default withRouter(ViewDepartment);
