import React, { Component } from 'react';
import { Form, FloatingLabel, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import withRouter from '../services/withRouter';
import { createStudent } from '../services/Student';

class CreateStudent extends Component {
    state = {
        name: '',
        roll: '',
        phone: '',
        department_id: '',
        status: 0,
        isLoading: false,
        errors: [],
        submitted: false
    }

    onChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        });
    }

    submitForm = async (e) => {
        e.preventDefault();
        const { history } = this.props;

        this.setState({isLoading: true});
        const dept_id = this.props.params.dept_id;
        
        const postbody = {
            name: this.state.name,
            roll: this.state.roll,
            phone: this.state.phone,
            department_id: dept_id,
            status: this.state.status
        }

        const res = await createStudent(postbody);

        if(res.success){
            this.setState({
                name: '',
                roll: '',
                phone: '',
                department_id: '',
                status: 0,
                isLoading: false,
                errors: [],
                submitted: true
            });
            history.push("/project-list");
        }else{
            this.setState({
                isLoading: false,
                errors: res.errors
            });
        }
    }

    render(){
        return(
            <>
                <h1>Create Student  

                <Link to={`/view-department/${this.props.params.dept_id}`}><Button variant="primary" className="btn-sm">Back</Button></Link>
                </h1>
                <Form onSubmit={this.submitForm}>
                    {this.state.submitted &&
                        <p className="alert alert-success">Your student has been created successfully</p>
                    }
                    <FloatingLabel controlId="floatingInput" label="Student Name" className="mb-3">
                        <Form.Control type="text" placeholder="Enter Student Name" name="name" value={this.state.name} onChange={(e) => this.onChange(e)} />
                    </FloatingLabel>
                    {this.state.errors && this.state.errors.name && 
                        <p className="alert alert-danger">{this.state.errors.name[0]}</p>
                    }
                    <FloatingLabel controlId="floatingInput" label="Roll" className="mb-3">
                        <Form.Control type="text" placeholder="Enter Roll" name="roll" value={this.state.roll} onChange={(e) => this.onChange(e)}/>
                    </FloatingLabel>
                    {this.state.errors && this.state.errors.roll && 
                        <p className="alert alert-danger">{this.state.errors.roll[0]}</p>
                    }
                    <FloatingLabel controlId="floatingInput" label="Phone" className="mb-3">
                        <Form.Control type="text" placeholder="Enter Phone Number" name="phone" value={this.state.phone} onChange={(e) => this.onChange(e)}/>
                    </FloatingLabel>
                    {this.state.errors && this.state.errors.phone && 
                        <p className="alert alert-danger">{this.state.errors.phone[0]}</p>
                    }
                    <Form.Check type="checkbox" id="status" label="status" name="status" value="2" onChange={(e) => this.onChange(e)}/>
                    
                    {this.state.isLoading && 
                        <Button variant="primary" type="button" className="mt-2" disabled>Saving...</Button>
                    }
                    
                    {!this.state.isLoading && 
                        <Button variant="primary" type="submit" className="mt-2">Save</Button>
                    }
                </Form>
            </>
        )
    }
}

export default withRouter(CreateStudent);
