import './App.css';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import NewTable from './components/Table';
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import NewAlert from './components/Alert';
import NewGrid from './components/Grid';
import NewForm from './components/Form';
import Layout from './components/Layout';
import NewList from './components/List';
import NewState from './components/States';
import Users from './components/Users';
import FormSubmit from './components/FormSubmit';
import Post from './components/Post';
import Departments from './pages/departments/Departments';
import EditDepartment from './pages/departments/Edit';
import CreateDepartment from './pages/departments/Create';
import ViewDepartment from './pages/departments/View';
import CreateStudent from './pages/students/create';
import EditStudent from './pages/students/edit';

function App() {
  return (

    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<NewTable />} />
          <Route path="alert" element={<NewAlert alert="Whoops!" />} />
          <Route path="grid" element={<NewGrid />} />
          <Route path="form" element={<NewForm />} />
          <Route path="list" element={<NewList />} />
          <Route path="states" element={<NewState />} />
          <Route path="users" element={<Users />} />
          <Route path="form-submit" element={<FormSubmit />} />
          <Route path="post" element={<Post />} />
          <Route path="departments" element={<Departments />} />
          <Route path="create-department" element={<CreateDepartment />} />
          <Route path={`view-department/:id`} element={<ViewDepartment />}/>
          <Route path="create-student/:dept_id" element={<CreateStudent />} />
          <Route path="edit-student/:dept_id/:id" element={<EditStudent />} />
          <Route path="edit-department/:id" element={<EditDepartment />} />
        </Route>
      </Routes>
    </BrowserRouter>

  );
}

export default App;
