import React, { Component } from 'react';
import { Table, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

class ViewDepartment extends Component {
    state = {
        name: '',
        address: '',
        status: 0,
        isLoading: false
    }

    componentDidMount(){
        this.departmentDetails();
        this.setState({isLoading:false});
    }

    departmentDetails = async(e) => {
        this.setState({isLoading:true});
        console.log("ok");
    }

    render(){
        return(
            <>
                <h1>View Department

                <Link to="/departments"><Button variant="primary" className="btn-sm">Back</Button></Link>
                </h1>

                <Table striped bordered hover variant="dark">
                    <tbody>
                        <tr>
                            <th>Name</th>
                            <td></td>
                        </tr>
                        <tr>
                            <th>Address</th>
                            <td></td>
                        </tr>
                        <tr>
                            <th>Status</th>
                            <td></td>
                        </tr>
                    </tbody>
                </Table>

                <h1>Students</h1>
                <Table striped bordered hover variant="dark">
                    <thead>
                        <tr>
                            <th>Head</th>
                            <th></th>
                            <th></th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <th>Name</th>
                            <td></td>
                            <td></td>
                            <td></td>
                        </tr>
                    </tbody>
                </Table>
                
            </>
        )
    }
}

export default ViewDepartment;
