import React, { Component } from "react";
import { Table, Button, Spinner, Badge } from "react-bootstrap";
import { Link } from "react-router-dom";
import { departmentDetails } from "../services/Departments";
import { useParams } from "react-router-dom";

class ViewDepartment extends Component {
  state = {
    isLoading: false,
    department: "",
    students: [],
  };

  componentDidMount() {
    this.getDepartmentDetails();
  }

  getDepartmentDetails = async () => {
    this.setState({ isLoading: true });
    const res = await departmentDetails(1);

    if (res.success) {
      this.setState({
        department: res.data,
        isLoading: false,
      });
    } else {
      this.setState({
        isLoading: false,
      });
      alert("something went wrong");
    }
  };

  render() {
    return (
      <>
        <h1>
          View Department
          <Link to="/departments">
            <Button variant="primary" className="btn-sm">
              Back
            </Button>
          </Link>
        </h1>

        {this.state.isLoading ? (
          <div className="text-center">
            <Spinner animation="border" role="status">
              <span className="visually-hidden">Loading...</span>
            </Spinner>
          </div>
        ) : (
          <div>
            <Table striped bordered hover variant="dark">
              <tbody>
                <tr>
                  <th>Name</th>
                  <td>{this.state.department.name}</td>
                </tr>
                <tr>
                  <th>Address</th>
                  <td>{this.state.department.address}</td>
                </tr>
                <tr>
                  <th>Status</th>
                  <td> 
                    {this.state.department.status ==1 ?
                        (
                          <Badge bg="success">Active</Badge>
                        ):(
                          <Badge bg="danger">Inactive</Badge>
                        )
                    }
                  </td>
                </tr>
              </tbody>
            </Table>

            <h1>Students</h1>
            <Table striped bordered hover variant="dark">
              <thead>
                <tr>
                  <th>Head</th>
                  <th> </th>
                  <th> </th>
                  <th> </th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <th>Name</th>
                  <td></td>
                  <td></td>
                  <td></td>
                </tr>
              </tbody>
            </Table>
          </div>
        )}
      </>
    );
  }
}

export default ViewDepartment;
