import Axios from "axios";

export const departmentList = async () => {
    return await Axios.get("http://127.0.0.1:8000/api/departments").then(res => {
        return res.data;
    });
}

export const storeDepartment = async (data) => {
    return await Axios.post("http://127.0.0.1:8000/api/departments", data).then(res => {
        return res.data;
    });
}

export const departmentDetails = async(id) => {
    return await Axios.get("http://127.0.0.1:8000/api/departments/" + id).then(res => {
        return res.data;
    })
}

export const updateDepartment = async (data, id) => {
    return await Axios.put("http://127.0.0.1:8000/api/departments/" + id, data).then(res => {
        return res.data;
    });
}
