import React, {Component} from "react";
import { Row, Col, Button, Card, Spinner } from 'react-bootstrap';
import Axios from "axios";
import { departmentList } from "../services/Departments";
import { Link } from "react-router-dom";

class DepartmentGrid extends Component{

    state = {
        departments: [],
        isLoading: false
    }

    componentDidMount(){
        this.getDepartments();
    }

    getDepartments = async(e) => {
        this.setState({
            isLoading: true
        });
        const res = await departmentList();
        const departments = res.data;
        
        if(res.success){
            this.setState({
                departments,
                isLoading: false
            })
        }
    }

    render(){
        return(
            <>
                {this.state.isLoading && (
                    <div className="text-center">
                        <Spinner animation="border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </Spinner>
                    </div>
                )}
                { this.state.departments.length > 0 && (
                    <Row>
                        {this.state.departments.map((department, index) => (
                            <Col sm={4} key={index} className="mb-4">
                                <Card className="text-center">
                                    <Card.Header>{ department.name }</Card.Header>
                                    <Card.Body>
                                        <Card.Text>
                                        { department.address }
                                        </Card.Text>
                                    </Card.Body>
                                    <Card.Footer className="text-muted">
                                        <Link to="/view-department/:id"><Button variant="in" className="btn-sm mr-2">Edit</Button></Link>
                                        <Link to="/edit-department"><Button variant="warning" className="btn-sm mr-2">Edit</Button></Link>
                                        <Button variant="danger" className="btn-sm mr-2">Delete</Button>
                                    </Card.Footer>
                                </Card>
                            </Col>
                        ))}
                    </Row>
                )}
            </>
        )
    }
}

export default DepartmentGrid;
