import React, {Component, useEffect, useState} from "react";
import { Table, Spinner, Button } from 'react-bootstrap';
import { departmentList, deleteDepartment } from "../services/Departments";
import { Link } from "react-router-dom";
import withRouter from "../services/withRouter";

function DepartmentList(){

    const [departments, setDepartments] = useState([]) ;
    const [isLoading, setIsLoading] = useState(false);
    const [del, setDel] = useState(false);

    const getDepartments = async (e) => {
        setIsLoading(true);
        const res = await departmentList();

        if(res.success){
            setDepartments(res.data);
            setIsLoading(false);
        }

    }

    const deleteDepartment = async (id) => {
        if(confirm("You can't recover this record")){
            console.log(id);
            setDel(true);
            const res = await deleteDepartment(id);
            console.log(res);
            if(res.success){
                getDepartments();
                setDel(false);
            }
        }
    }

    useEffect (() => {
        getDepartments();
    }, []);

    var i =1;
    
        return(
            <>
                {isLoading && (
                    <div className="text-center">
                        <Spinner animation="border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </Spinner>
                    </div>
                )}
                { departments.length > 0 && (
                    <Table striped bordered hover variant="dark">
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Name</th>
                                <th>Address</th>
                                <th>Status</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {departments.map (department => (
                                <tr key={i++}>
                                    <td>{ i }</td>
                                    <td>{ department.name }</td>
                                    <td>{ department.address }</td>
                                    <td>{ department.status == 1 ? ("Active") : ("Disabled") }</td>
                                    <td>
                                        <Link to={`/view-department/${ department.id }`}><Button variant="info" className="btn-sm mr-2">View</Button></Link> 
                                        <Link to={`/edit-department/${ department.id }`}><Button variant="warning" className="btn-sm mr-2">Edit</Button></Link> 
                                        <Button variant="danger" className="btn-sm mr-2" onClick={ deleteDepartment.bind(department.id) }>Delete</Button>
                                    </td>
                                </tr>
                            ))}
                            
                        </tbody>
                    </Table>
                )}
            </>
        )
}

export default withRouter(DepartmentList);
