import React, {Component} from "react";
import { Row, Col, Button, Card, Spinner } from 'react-bootstrap';
import { deleteDepartment, departmentList } from "../services/Departments";
import { Link } from "react-router-dom";

class DepartmentGrid extends Component{

    state = {
        departments: [],
        isLoading: false,
        deleted: false
    }

    componentDidMount(){
        this.getDepartments();
    }

    getDepartments = async(e) => {
        this.setState({
            isLoading: true
        });
        const res = await departmentList();
        const departments = res.data;
        
        if(res.success){
            this.setState({
                departments,
                isLoading: false
            })
        }
    }

    deleteDepartment = async (id) => {
        if(confirm("Are you sure?")){
            const res = await deleteDepartment(id);
            if(res.success){
                this.getDepartments();
                this.setState({
                    deleted: true,
                });
            }
        }
    }

    render(){
        return(
            <>
                {this.state.isLoading && (
                    <div className="text-center">
                        <Spinner animation="border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </Spinner>
                    </div>
                )}
                
                {this.state.deleted && (
                    <div className="alert alert-success">
                        Department has been deleted successfully.
                    </div>
                )}

                { this.state.departments.length > 0 && (
                    <Row>
                        {this.state.departments.map((department, index) => (
                            <Col sm={4} key={index} className="mb-4">
                                <Card className="text-center">
                                    <Card.Header>{ department.name }</Card.Header>
                                    <Card.Body>
                                        <Card.Text>
                                        { department.address }
                                        </Card.Text>
                                    </Card.Body>
                                    <Card.Footer className="text-muted">
                                        <Link to={ `/view-department/${department.id}` }><Button variant="info" className="btn-sm mr-2">View</Button></Link>
                                        <Link to={`/edit-department/${department.id}`}><Button variant="warning" className="btn-sm mr-2">Edit</Button></Link>
                                        <Button variant="danger" className="btn-sm mr-2" onClick={(e) => this.deleteDepartment(department.id)}>Delete</Button>
                                    </Card.Footer>
                                </Card>
                            </Col>
                        ))}
                    </Row>
                )}
            </>
        )
    }
}

export default DepartmentGrid;
