import React, {Component} from "react";
import DepartmentGrid from "./DepartmentsGrid";
import DepartmentList from "./DepartmentsList";
import { Button } from 'react-bootstrap';
import { Link } from "react-router-dom";

class Departments extends Component{
    
    state = {
        layoutType : 1
    }

    changeLayout = (a) =>{
        this.setState ({
            layoutType : a
        })
    }

    render (){
        return(
            <>
                <h1>Departments
                    <Button variant="info" className="btn-sm ml-2 mr-2" onClick={ () => this.changeLayout(1) }>
                        List View
                    </Button>
                    <Button variant="success" className="btn-sm ml-2 mr-2 text-light" onClick={ () => this.changeLayout(2) }>
                        Grid View
                    </Button>
                    <Link to="/create-department"><Button className="btn btn-primary btn-sm text-right">Create Department</Button></Link>
                </h1>
                { this.state.layoutType == 1 ? (
                    <DepartmentList />
                ) : (
                    <DepartmentGrid />
                ) }
            </>
        )
    }
}

export default Departments;
