import React, { Component } from 'react';
import { Form, FloatingLabel, Button } from 'react-bootstrap';

class CreateDepartment extends Component{
    state = {
        name: '',
        address: '',
        status: 0
    }

    onChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    render(){
        return(
            <>
                <h1>Create Department</h1>
                <FloatingLabel controlId="floatingInput" label="Department Name" className="mb-3">
                    <Form.Control type="text" placeholder="Enter Department Name" onChange={(e) => this.onChange(e)} />
                </FloatingLabel>
                <FloatingLabel controlId="floatingInput" label="Address" className="mb-3">
                    <Form.Control type="text" placeholder="Enter Address" onChange={(e) => this.onChange(e)}/>
                </FloatingLabel>
                <Form.Check type="checkbox" id="status" label="status" value="1" onChange={(e) => this.onChange(e)}/>
                <Button variant="primary" className="mt-2">Save</Button>{' '}
            </>
        )
    }
}

export default CreateDepartment;
